package com.example.exam.service; import com.example.exam.model.Question; import com.example.exam.repository.QuestionRepository; import org.springframework.stereotype.Service; import java.util.*; @Service public class QuestionService { private final QuestionRepository repo; public QuestionService(QuestionRepository repo){this.repo=repo;} public Question create(Question q){return repo.save(q);} public List<Question> byExam(Long examId){return repo.findByExamId(examId);} }